function criarCardProjeto(a){const s=a.tags.map(a=>{let s="badge-neutro";return"Educação"===a&&(s="badge-primario"),"Alimentação"===a&&(s="badge-secundario"),`<span class="badge ${s}">${a}</span>`}).join("");return`\n        <article class="card col-md-4 projeto-card">\n            <h3>${a.titulo}</h3>\n            <p>${a.descricao}</p>\n            <div class="tags-container">${s}</div>\n            <p class="status">Status: \n                <span class="badge badge-alerta">${a.status}</span>\n            </p>\n            <a href="#" class="btn btn-primario">Ver Detalhes</a>\n        </article>\n    `}function criarConteudoProjetos(){const a=DADOS_PROJETOS.map(criarCardProjeto).join("");return`\n        <section id="lista-projetos" class="grid-12">\n            <h2 class="col-12" style="text-align: center;">Nossos Projetos Atuais</h2>\n            ${a}\n        </section>\n        <section id="chamada-voluntario" class="grid-12" style="margin-top: 40px; text-align: center;">\n            <div class="alert alert-info col-12">\n                Quer fazer a diferença? <a href="cadastro.html">Cadastre-se para participar</a> de um desses projetos!\n            </div>\n        </section>\n    `}const DADOS_PROJETOS=[{titulo:"Alfabetização Digital",descricao:"Aulas semanais para idosos e jovens de baixa renda, focando em uso seguro da internet e ferramentas básicas.",tags:["Educação","Inclusão","Tecnologia"],status:"Ativo"},{titulo:"Banco de Alimentos Comunitário",descricao:"Coleta e distribuição mensal de cestas básicas para 50 famílias cadastradas na região metropolitana.",tags:["Alimentação","Assistência Social"],status:"Urgente"},{titulo:"Saúde em Dia",descricao:"Parceria com clínicas locais para oferecer consultas e exames básicos gratuitos uma vez por mês.",tags:["Saúde","Bem-Estar"],status:"Aguardando Voluntários"}];